package _05_2FOR_Q;

public class _01_2FOR_Q {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
					
				/*	2. 
					*****
					****
					***
					**
					*           */
		
			for(int i =0; i<5; i++) {
				//System.out.println("*");
				for(int j=4; j>=i; j--) {
					System.out.print("*");
				}
				System.out.println();
			}

	}

}
